mapper.select:

SELECT ISBN, UNIT_NUM, QUESTION_NUM, EXPLANATION, QUESTION_FILE
FROM QUESTION
WHERE ISBN = #{isbn} AND UNIT_NUM = #{unit_num} AND QUESTION_NUM = #{question_num}


SELECT ISBN, UNIT_NUM, QUESTION_NUM, EXPLANATION, QUESTION_FILE
FROM QUESTION
WHERE ISBN = #{isbn}


SELECT ISBN, UNIT_NUM, QUESTION_NUM, EXPLANATION, QUESTION_FILE
FROM QUESTION
