mapper.delete:

DELETE QUESTION WHERE ISBN = #{lecture_num} AND
UNIT_NUM = #{unit_num} AND
QUESTION_NUM = #{question_num}

mapper.update:

UPDATE QUESTION SET ISBN = #{isbn}, UNIT_NUM = #{unit_num}, QUESTION_NUM = #{question_num} 
WHERE ISBN = #{lecture_num} AND
UNIT_NUM = #{unit_num} AND
QUESTION_NUM = #{question_num}
